import Row from './Row.js'
import Link from 'next/link'
import React from 'react'

/*
export default function NavBar(props) {
  return (
    <Row justify="flex-end" position={props.position} class="scroll">
      <Link href="#about"><a>About</a></Link>
      <Link href="#skills"><a>Skills</a></Link>
      <Link href="#experience"><a>Experience</a></Link>
      <Link href="#education"><a>Education</a></Link>
      <Link href="#socialize"><a>Socialize</a></Link>
      <Link href="#contact"><a>Contact</a></Link>
      <style jsx>{`
        a {
          padding: 1rem 1.5rem;
          text-decoration: none;
          text-transform: uppercase;
        }

        a:hover {
          background-color: #555;
        }

        a:active {
          background-color: black;
        }

        a:visited {
          background-color: #ccc;
        }
      `}</style>
    </Row>
  )
}
*/
export default class NavBar extends React.Component {
  constructor(props) {
    super(props);
    this.handleScroll = this.handleScroll.bind(this);
  }

  refCallBack = element => {
    if(element) {
      this.props.getSize(element.getBoundingClientRect());
    }
  };

  componentDidMount() {
    //this.setState({navPos: window.innerHeight});
    window.addEventListener('scroll', this.handleScroll);
  };

  componentWillUnmount() {
    window.removeEventListener('scroll', this.handleScroll);
  };

  handleScroll(event) {
    if(event) {
      console.log(event.getBoundingClientRect().top);
    }
    //console.log(window.scrollY);

    /*this.scrollPos = window.scrollY;
    if(this.scrollPos > this.state.navPos){
      this.setState({navPos: this.scrollPos});
    }*/
  };

  render() {
    const children = React.Children.map(this.props.children, child => {
      return React.cloneElement(child, {
        position: this.state.navPos
      });
    });

    return (
      <div>
        {children}
        <style jsx>{`
          div {
            display: flex;
            flex-direction: column;
            width: 100%;
          }
        `}</style>
      </div>
    );
  }
}
