import Row from './Row.js'
import Link from 'next/link'
import React from 'react'

/*
export default function NavBar(props) {
  return (
    <Row justify="flex-end" position={props.position} class="scroll">
      <Link href="#about"><a>About</a></Link>
      <Link href="#skills"><a>Skills</a></Link>
      <Link href="#experience"><a>Experience</a></Link>
      <Link href="#education"><a>Education</a></Link>
      <Link href="#socialize"><a>Socialize</a></Link>
      <Link href="#contact"><a>Contact</a></Link>
      <style jsx>{`
        a {
          padding: 1rem 1.5rem;
          text-decoration: none;
          text-transform: uppercase;
        }

        a:hover {
          background-color: #555;
        }

        a:active {
          background-color: black;
        }

        a:visited {
          background-color: #ccc;
        }
      `}</style>
    </Row>
  )
}
*/
export default class NavBar extends React.Component {
  constructor(props) {
    super(props);
    this.handleScroll = this.handleScroll.bind(this);
  }

  refCallBack = element => {
    if(element) {
      this.props.getSize(element.getBoundingClientRect());
    }
  };

  componentDidMount() {
    //this.setState({navPos: window.innerHeight});
    window.addEventListener('scroll', this.handleScroll);
  };

  componentWillUnmount() {
    window.removeEventListener('scroll', this.handleScroll);
  };

  handleScroll(event) {
    console.log(this.div.getBoundingClientRect().top);
    //console.log(window.scrollY);

    /*this.scrollPos = window.scrollY;
    if(this.scrollPos > this.state.navPos){
      this.setState({navPos: this.scrollPos});
    }*/
  };

  render() {
    return (
      <Row justify="flex-end" navbar={true}>
        <Link href="#about"><a>About</a></Link>
        <Link href="#skills"><a>Skills</a></Link>
        <Link href="#experience"><a>Experience</a></Link>
        <Link href="#education"><a>Education</a></Link>
        <Link href="#socialize"><a>Socialize</a></Link>
        <Link href="#contact"><a>Contact</a></Link>
        <style jsx>{`
          a {
            padding: 1rem 1.5rem;
            text-decoration: none;
            text-transform: uppercase;
          }

          a:hover {
            background-color: #555;
          }

          a:active {
            background-color: black;
          }

          a:visited {
            background-color: #ccc;
          }
        `}</style>
      </Row>
    );
  }
}
