export default class Row extends React.Component {
  constructor(props) {
    super(props);
    this.state = {sticky: false};
    this.scrollPos = 0;
  }

  componentDidMount() {
    //this.setState({navPos: window.innerHeight});
    window.addEventListener('scroll', this.handleScroll);
  };

  componentWillUnmount() {
    window.removeEventListener('scroll', this.handleScroll);
  };

  handleScroll(event) {
    this.scrollPos = window.scrollY;

    if(this.divRef.getBoundingClientRect().top < this.props.scrollPosition && this.props.navbar) {
        this.setState({sticky: true});
    }
    else {
      this.setState({sticky: false});
    }
  };

  render() {
    return (
      <div name={props.name} className={this.state.sticky ? "sticky" : ""} ref={element => this.divRef = element}>
        {props.children}
        <style jsx>{`
          div {
            display: flex;
            flex-direction: row;
            flex-wrap: wrap;
            flex-grow: 1;
            height: ${props.height};
            justify-content: ${props.justify};
          }
        `}</style>
      </div>
    );
  }
}
